mapper.select:
select * from setmeal

    and category_id=#{categoryId}
    and name like concat('%',#{name},'%')
    and status=#{status}

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
